@import './markdown.less';

body {
  position: relative;
  font-family: 'Chinese Quote', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'PingFang SC',
    'Hiragino Sans GB', 'Microsoft YaHei', 'Helvetica Neue', Helvetica, Arial, sans-serif, 'Apple Color Emoji',
    'Segoe UI Emoji', 'Segoe UI Symbol';
  font-size: 14px;
  line-height: 2;
  color: #555;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  margin: 0;
  padding: 0;
  font-weight: bold;
  line-height: 1.5;
  font-family: Baskerville,Georgia,"Liberation Serif",Kaiti SC,STKaiti,AR PL UKai CN,AR PL UKai HK,AR PL UKai TW,AR PL UKai TW MBE,AR PL KaitiM GB,KaiTi,KaiTi_GB2312,DFKai-SB,TW-Kai,serif;
}

@color: #bada55;

#nprogress .bar {
  background: @color;
}

#nprogress .peg {
  box-shadow: 0 0 10px @color, 0 0 5px @color;
}

#nprogress .spinner-icon {
  border-top-color: @color;
  border-left-color: @color;
}

.ant-back-top-icon {
  background-size: contain;
}

.drawer-btn {
  position: fixed;
  width: 41px;
  height: 41px;
  top: 100px;
  right: 0;
  cursor: pointer;
  box-shadow: 2px 0 8px rgba(0, 0, 0, 0.15);
  border-radius: 4px 0 0 4px;
  text-align: center;
  line-height: 41px;
  z-index: 999;
  background: #fff;
}

@media screen and (max-width: 1300px) {
  .content-inner-wrapper {
    // padding: 0 20px !important;
  }
  .ant-anchor {
    left: -20px;
  }
  .ant-anchor-ink::before {
    display: none;
  }
}

.pagination {
  text-align: right;
  margin-bottom: 20px;
}
